function [stateGuess,controlGuess] =LISAGuessData(timeVector,timeVectorType,numControls,phaseIdx)

global configEarth configSun phaseList

%  Load the data and initialize arrays
guessData = GetGuessData();
numTimes = size(timeVector,1);
stateGuess = zeros(numTimes,7);

% Compute the state guess
for i = 1:numTimes
    state = interp1(guessData(:,1)*86400,guessData(:,2:8),timeVector(i)*configEarth.TU);
    state(1) = state(1)/configEarth.DU;
    state(7) = state(7)/configEarth.MU;
    stateGuess(i,:) = state;
end

%  Compute the control guess
disp('Final Guess Time, days')
disp(timeVector(end)*configEarth.TU/86400)
disp('Final MLONG, Rad')
disp(state(end,6))
for i = 1:numTimes-1
    controlGuess(i,:) = [0 1 1 1];
end

end

